// src/components/SwipeCard.jsx

import React, { useState } from 'react';
import { useSwipeable } from 'react-swipeable';
import { motion, AnimatePresence } from 'framer-motion';
import './SwipeCard.css';
import axios from 'axios';

const SwipeCard = ({ slang, onSwipe }) => {
  const [showMeaning, setShowMeaning] = useState(false);
  const [swiped, setSwiped] = useState(false);
  const [direction, setDirection] = useState(null);

  const handleTTS = () => {
    const audio = new Audio(`https://swipelang-server4.onrender.com/tts?phrase=${encodeURIComponent(slang.phrase)}`);
    audio.play();
  };

  const handleSwipe = (dir) => {
    if (swiped) return; // prevent double swipe
    setSwiped(true);
    setDirection(dir);
    onSwipe(dir, slang.phrase);
  };

  const handlers = useSwipeable({
    onSwipedLeft: () => handleSwipe('left'),
    onSwipedRight: () => handleSwipe('right'),
    preventDefaultTouchmoveEvent: true,
    trackMouse: true
  });

  return (
    <div {...handlers} className="swipe-wrapper">
      <AnimatePresence>
        {!swiped && (
          <motion.div
            className="card"
            initial={{ x: 0, opacity: 1 }}
            animate={{ x: 0, opacity: 1 }}
            exit={{ x: direction === 'left' ? -200 : 200, opacity: 0 }}
            transition={{ duration: 0.4 }}
          >
            <h2>{slang.phrase}</h2>

            <button onClick={handleTTS}>üîä Î∞úÏùå Îì£Í∏∞</button>
            <button onClick={() => setShowMeaning(!showMeaning)}>üìñ Ìï¥ÏÑù Î≥¥Í∏∞</button>

            {showMeaning && (
              <div className="meaning">
                <p><strong>Îúª:</strong> {slang.meaning}</p>
                {slang.example && <p><strong>ÏòàÎ¨∏:</strong> {slang.example}</p>}
              </div>
            )}
          </motion.div>
        )}
      </AnimatePresence>
    </div>
  );
};

export default SwipeCard;
