// src/components/SwipeCard.jsx
import React, { useState } from 'react';
import TinderCard from 'react-tinder-card';
import axios from 'axios';
import './SwipeCard.css';

const SwipeCard = ({ slang, onSwipe }) => {
  const [showMeaning, setShowMeaning] = useState(false);

  const handleSwipe = (direction) => {
    onSwipe(direction, slang.phrase);
  };

  const handleTTS = () => {
    const audio = new Audio(`https://swipelang-server4.onrender.com/tts?phrase=${encodeURIComponent(slang.phrase)}`);
    audio.play();
  };

  return (
    <TinderCard
      className="swipe"
      onSwipe={handleSwipe}
      preventSwipe={['up', 'down']}
    >
      <div className="card">
        <h2>{slang.phrase}</h2>

        {/* üîä Î∞úÏùå Îì£Í∏∞ */}
        <button onClick={handleTTS}>üîä Î∞úÏùå Îì£Í∏∞</button>

        {/* üìñ Ìï¥ÏÑù Î≥¥Í∏∞ */}
        <button onClick={() => setShowMeaning(!showMeaning)}>üìñ Ìï¥ÏÑù Î≥¥Í∏∞</button>

        {showMeaning && (
          <div className="meaning">
            <p><strong>Îúª:</strong> {slang.meaning}</p>
            {slang.example && <p><strong>ÏòàÎ¨∏:</strong> {slang.example}</p>}
          </div>
        )}
      </div>
    </TinderCard>
  );
};

export default SwipeCard;
